import React, { useState } from 'react';
import { View, StyleSheet, ScrollView, Platform, KeyboardAvoidingView, Alert } from 'react-native';
import { Text, Button, TextInput, useTheme, Menu, TouchableRipple, HelperText, ProgressBar } from 'react-native-paper';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import DateTimePicker from '@react-native-community/datetimepicker';
import { MaterialCommunityIcons } from '@expo/vector-icons'; // SỬ DỤNG ICON TỪ ĐÂY
import { appointmentService } from '../services/appointment.service';
import dayjs from 'dayjs';
import { useNavigation } from '@react-navigation/native';

export default function BookingScreen() {
    const theme = useTheme();
    const navigation = useNavigation<any>();
    const queryClient = useQueryClient();

    // --- FORM STATE ---
    const [selectedBranch, setSelectedBranch] = useState<string>('');
    const [selectedSpecialty, setSelectedSpecialty] = useState<string>('');
    const [selectedDoctor, setSelectedDoctor] = useState<string>('');
    const [date, setDate] = useState(new Date());
    const [showDatePicker, setShowDatePicker] = useState(false);
    const [notes, setNotes] = useState('');

    // --- UI STATE (Dropdown Menus) ---
    const [menuBranchVisible, setMenuBranchVisible] = useState(false);
    const [menuSpecialtyVisible, setMenuSpecialtyVisible] = useState(false);
    const [menuDoctorVisible, setMenuDoctorVisible] = useState(false);

    // --- DATA FETCHING ---
    const { data: branches } = useQuery({ queryKey: ['branches'], queryFn: appointmentService.getBranches });
    const { data: specialties } = useQuery({ queryKey: ['specialties'], queryFn: appointmentService.getSpecialties });

    // Fetch doctors based on filters
    const { data: doctors, isFetching: loadingDoctors } = useQuery({
        queryKey: ['doctors', selectedBranch, selectedSpecialty],
        queryFn: () => appointmentService.getDoctors(selectedBranch, selectedSpecialty),
        enabled: !!selectedBranch,
    });

    // --- MUTATION (Gửi đặt lịch) ---
    const createMutation = useMutation({
        mutationFn: appointmentService.createAppointment,
        onSuccess: () => {
            Alert.alert('Thành công', 'Đặt lịch khám thành công!');
            queryClient.invalidateQueries(['myAppointments']);
            navigation.navigate('Appointments');
        },
        onError: (err: any) => {
            Alert.alert('Lỗi', err?.response?.data?.message || 'Không thể đặt lịch');
        },
    });

    // --- HANDLERS ---
    const handleDateChange = (event: any, selectedDate?: Date) => {
        setShowDatePicker(Platform.OS === 'ios');
        if (selectedDate) setDate(selectedDate);
    };

    const handleSubmit = () => {
        if (!selectedBranch) {
            Alert.alert('Thiếu thông tin', 'Vui lòng chọn cơ sở khám');
            return;
        }
        if (!notes.trim()) {
            Alert.alert('Thiếu thông tin', 'Vui lòng nhập triệu chứng/lý do khám');
            return;
        }

        createMutation.mutate({
            branch_id: selectedBranch,
            doctor_id: selectedDoctor || undefined,
            start_time: date.toISOString(),
            notes: notes,
        });
    };

    const getBranchName = () => branches?.find((b) => b.id === selectedBranch)?.name || 'Chọn chi nhánh';
    const getSpecialtyName = () => specialties?.find((s) => s.id === selectedSpecialty)?.name || 'Tất cả chuyên khoa';
    const getDoctorName = () => doctors?.find((d) => d.id === selectedDoctor)?.user.full_name || 'Chọn bác sĩ (hoặc để trống)';

    return (
        <KeyboardAvoidingView behavior={Platform.OS === 'ios' ? 'padding' : undefined} style={{ flex: 1 }}>
            <ScrollView style={styles.container} contentContainerStyle={{ paddingBottom: 40 }}>

                {/* STEP INDICATOR */}
                <View style={styles.stepContainer}>
                    <Text variant="titleMedium" style={{ fontWeight: 'bold', color: theme.colors.primary }}>
                        1. Thông tin khám
                    </Text>
                    <ProgressBar progress={0.5} color={theme.colors.primary} style={{ marginTop: 8, borderRadius: 4 }} />
                </View>

                <View style={styles.formContainer}>
                    {/* 1. CHỌN CƠ SỞ */}
                    <View style={styles.inputGroup}>
                        <Text style={styles.label}>Cơ sở khám <Text style={{ color: 'red' }}>*</Text></Text>
                        <Menu
                            visible={menuBranchVisible}
                            onDismiss={() => setMenuBranchVisible(false)}
                            anchor={
                                <TouchableRipple onPress={() => setMenuBranchVisible(true)} style={styles.dropdown}>
                                    <View style={styles.dropdownContent}>
                                        <Text style={{ color: selectedBranch ? 'black' : '#777' }}>{getBranchName()}</Text>
                                        {/* Sửa lỗi Icon ở đây */}
                                        <MaterialCommunityIcons name="chevron-down" size={24} color="#777" />
                                    </View>
                                </TouchableRipple>
                            }
                        >
                            {branches?.map((branch) => (
                                <Menu.Item
                                    key={branch.id}
                                    onPress={() => { setSelectedBranch(branch.id); setMenuBranchVisible(false); }}
                                    title={branch.name}
                                />
                            ))}
                        </Menu>
                    </View>

                    {/* 2. CHỌN CHUYÊN KHOA */}
                    <View style={styles.inputGroup}>
                        <Text style={styles.label}>Chuyên khoa (Lọc bác sĩ)</Text>
                        <Menu
                            visible={menuSpecialtyVisible}
                            onDismiss={() => setMenuSpecialtyVisible(false)}
                            anchor={
                                <TouchableRipple onPress={() => setMenuSpecialtyVisible(true)} style={styles.dropdown}>
                                    <View style={styles.dropdownContent}>
                                        <Text style={{ color: selectedSpecialty ? 'black' : '#777' }}>{getSpecialtyName()}</Text>
                                        <MaterialCommunityIcons name="chevron-down" size={24} color="#777" />
                                    </View>
                                </TouchableRipple>
                            }
                        >
                            <Menu.Item onPress={() => { setSelectedSpecialty(''); setMenuSpecialtyVisible(false); }} title="Tất cả chuyên khoa" />
                            {specialties?.map((spec) => (
                                <Menu.Item
                                    key={spec.id}
                                    onPress={() => { setSelectedSpecialty(spec.id); setMenuSpecialtyVisible(false); }}
                                    title={spec.name}
                                />
                            ))}
                        </Menu>
                    </View>

                    {/* 3. CHỌN BÁC SĨ */}
                    <View style={styles.inputGroup}>
                        <Text style={styles.label}>Bác sĩ khám</Text>
                        <Menu
                            visible={menuDoctorVisible}
                            onDismiss={() => setMenuDoctorVisible(false)}
                            anchor={
                                <TouchableRipple
                                    onPress={() => !selectedBranch ? Alert.alert('Lưu ý', 'Vui lòng chọn cơ sở trước') : setMenuDoctorVisible(true)}
                                    style={[styles.dropdown, !selectedBranch && { backgroundColor: '#f0f0f0' }]}
                                >
                                    <View style={styles.dropdownContent}>
                                        <Text style={{ color: selectedDoctor ? 'black' : '#777' }}>
                                            {loadingDoctors ? 'Đang tải...' : getDoctorName()}
                                        </Text>
                                        <MaterialCommunityIcons name="chevron-down" size={24} color="#777" />
                                    </View>
                                </TouchableRipple>
                            }
                        >
                            <Menu.Item onPress={() => { setSelectedDoctor(''); setMenuDoctorVisible(false); }} title="Chọn bác sĩ ngẫu nhiên" />
                            {doctors?.map((doc) => (
                                <Menu.Item
                                    key={doc.id}
                                    onPress={() => { setSelectedDoctor(doc.id); setMenuDoctorVisible(false); }}
                                    title={doc.user.full_name}
                                />
                            ))}
                        </Menu>
                        {!selectedBranch && <HelperText type="info">Vui lòng chọn cơ sở để xem danh sách bác sĩ</HelperText>}
                    </View>

                    {/* 4. CHỌN NGÀY GIỜ */}
                    <View style={styles.inputGroup}>
                        <Text style={styles.label}>Ngày khám <Text style={{ color: 'red' }}>*</Text></Text>
                        <TouchableRipple onPress={() => setShowDatePicker(true)} style={styles.dropdown}>
                            <View style={styles.dropdownContent}>
                                <Text>{dayjs(date).format('DD/MM/YYYY HH:mm')}</Text>
                                <MaterialCommunityIcons name="calendar" size={24} color="#777" />
                            </View>
                        </TouchableRipple>
                        {showDatePicker && (
                            <DateTimePicker
                                value={date}
                                mode="date"
                                display={Platform.OS === 'ios' ? 'spinner' : 'default'}
                                onChange={handleDateChange}
                                minimumDate={new Date()}
                            />
                        )}
                    </View>

                    {/* 5. TRIỆU CHỨNG */}
                    <View style={styles.inputGroup}>
                        <Text style={styles.label}>Triệu chứng / Lý do <Text style={{ color: 'red' }}>*</Text></Text>
                        <TextInput
                            mode="outlined"
                            placeholder="Nhập triệu chứng (VD: Đau đầu, sốt...)"
                            multiline
                            numberOfLines={4}
                            value={notes}
                            onChangeText={setNotes}
                            style={{ backgroundColor: 'white' }}
                        />
                    </View>

                    {/* BUTTONS */}
                    <View style={styles.buttonRow}>
                        <Button mode="outlined" onPress={() => navigation.goBack()} style={{ flex: 1 }}>
                            Quay lại
                        </Button>
                        <View style={{ width: 12 }} />
                        <Button
                            mode="contained"
                            onPress={handleSubmit}
                            loading={createMutation.isPending}
                            style={{ flex: 1 }}
                        >
                            Tiếp tục
                        </Button>
                    </View>

                </View>
            </ScrollView>
        </KeyboardAvoidingView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    stepContainer: {
        padding: 16,
        backgroundColor: '#f9f9f9',
