import axiosInstance from '../lib/axios';

export interface Appointment {
  id: string;
  patient_id: string;
  doctor_assigned_id: string;
  room_id: string;
  start_time: string;
  end_time: string;
  status: string;
  appointment_type: string;
  notes?: string;
  doctor?: {
    code: string;
    title: string;
    user: {
      full_name: string;
    };
  };
  room?: {
    name: string;
    code: string;
  };
}

export interface AppointmentListResponse {
  data: Appointment[];
  pagination: {
    page: number;
    limit: number;
    total: number;
    totalPages: number;
  };
}

// Thêm các interface mới
export interface Branch {
  id: string;
  name: string;
  address: string;
}

export interface Specialty {
  id: string;
  name: string;
}

export interface Doctor {
  id: string;
  user: {
    full_name: string;
  };
  specialty_id: string;
}

export interface CreateAppointmentDto {
  doctor_id?: string;
  branch_id: string;
  start_time: string; // ISO String
  notes: string;
}

export const appointmentService = {
  getMyAppointments: (): Promise<AppointmentListResponse> => {
    return axiosInstance.get('/appointments').then((res) => res.data);
  },

  getAppointmentById: (id: string): Promise<Appointment> => {
    return axiosInstance.get(`/appointments/${id}`).then((res) => res.data);
  },



  // --- THÊM MỚI ---
  getBranches: (): Promise<Branch[]> => {
    return axiosInstance.get('/branches').then((res) => res.data);
  },

  getSpecialties: (): Promise<Specialty[]> => {
    return axiosInstance.get('/specialties').then((res) => res.data);
  },

  getDoctors: (branchId?: string, specialtyId?: string): Promise<Doctor[]> => {
    const params: any = {};
    if (branchId) params.branch_id = branchId;
    if (specialtyId) params.specialty_id = specialtyId;
    return axiosInstance.get('/doctors', { params }).then((res) => res.data);
  },

  createAppointment: (data: CreateAppointmentDto): Promise<any> => {
    return axiosInstance.post('/appointments', data).then((res) => res.data);
  },
};
