import axios from 'axios';

// Test connection to backend
export const testConnection = async () => {
  const API_BASE_URL = 'http://192.168.100.248:3000/api';

  console.log('üîç Testing connection to:', API_BASE_URL);

  try {
    const response = await axios.get(`${API_BASE_URL}/auth/me`, {
      timeout: 5000,
      validateStatus: () => true, // Accept any status
    });

    console.log('‚úÖ Connected! Status:', response.status);
    console.log('üì¶ Response:', response.data);
    return true;
  } catch (error: any) {
    console.error('‚ùå Connection failed!');

    if (error.code === 'ECONNABORTED') {
      console.error('‚è±Ô∏è Timeout - Backend kh√¥ng ph·∫£n h·ªìi');
    } else if (error.code === 'ECONNREFUSED') {
      console.error('üö´ Connection refused - Backend kh√¥ng ch·∫°y ho·∫∑c firewall block');
    } else if (error.message.includes('Network')) {
      console.error('üì° Network error - Ki·ªÉm tra WiFi v√† IP');
    } else {
      console.error('Error:', error.message);
    }

    return false;
  }
};
