import React, { useMemo } from 'react';
import { View, StyleSheet, ScrollView, RefreshControl, TouchableOpacity, Image, Dimensions } from 'react-native';
import { Text, useTheme, Avatar, Card, Button, Surface, IconButton } from 'react-native-paper';
import { useQuery } from '@tanstack/react-query';
import { useNavigation } from '@react-navigation/native';
import { MaterialCommunityIcons } from '@expo/vector-icons';
import { appointmentService } from '../services/appointment.service';
import { useAuthStore } from '../stores/authStore';
import dayjs from 'dayjs';
import 'dayjs/locale/vi';

// Cấu hình ngôn ngữ tiếng Việt cho ngày tháng
dayjs.locale('vi');

const { width } = Dimensions.get('window');

export default function HomeScreen() {
  const theme = useTheme();
  const navigation = useNavigation<any>();
  const user = useAuthStore((state) => state.user);

  // Lấy danh sách lịch hẹn để tìm lịch sắp tới
  const { data: appointmentsData, isLoading, refetch } = useQuery({
    queryKey: ['myAppointments'],
    queryFn: appointmentService.getMyAppointments,
  });

  // Logic tìm lịch hẹn sắp tới gần nhất (Next Appointment)
  const nextAppointment = useMemo(() => {
    if (!appointmentsData?.data) return null;
    const now = dayjs();

    // Lọc lịch chưa hoàn thành và thời gian > hiện tại
    const upcoming = appointmentsData.data
      .filter((apt: any) =>
        (apt.status === 'SCHEDULED' || apt.status === 'CONFIRMED') &&
        dayjs(apt.start_time).isAfter(now)
      )
      .sort((a: any, b: any) => dayjs(a.start_time).diff(dayjs(b.start_time)));

    return upcoming.length > 0 ? upcoming[0] : null;
  }, [appointmentsData]);

  // Các chức năng chính (Menu Grid)
  const features = [
    {
      id: 1,
      title: 'Đặt lịch khám',
      icon: 'calendar-plus',
      color: '#4CAF50',
      screen: 'Appointments' // Tên Tab hoặc Screen cần navigate
    },
    {
      id: 2,
      title: 'Đơn thuốc',
      icon: 'pill',
      color: '#2196F3',
      screen: 'Prescriptions'
    },
    {
      id: 3,
      title: 'Hóa đơn',
      icon: 'receipt',
      color: '#FF9800',
      screen: 'Invoices'
    },
    {
      id: 4,
      title: 'Hồ sơ cá nhân',
      icon: 'account-details',
      color: '#9C27B0',
      screen: 'Profile'
    },
  ];

  return (
    <ScrollView
      style={styles.container}
      refreshControl={<RefreshControl refreshing={isLoading} onRefresh={refetch} />}
    >
      {/* 1. HEADER SECTION (Chào hỏi) */}
      <View style={[styles.headerContainer, { backgroundColor: theme.colors.primary }]}>
        <View style={styles.headerContent}>
          <View>
            <Text variant="bodyLarge" style={{ color: 'rgba(255,255,255,0.8)' }}>Xin chào,</Text>
            <Text variant="headlineSmall" style={{ color: '#fff', fontWeight: 'bold' }}>
              {user?.full_name || 'Bệnh nhân'}
            </Text>
          </View>
          <TouchableOpacity onPress={() => navigation.navigate('Profile')}>
            <Avatar.Text
              size={50}
              label={user?.full_name ? user.full_name.charAt(0).toUpperCase() : 'U'}
              style={{ backgroundColor: '#fff' }}
              color={theme.colors.primary}
            />
          </TouchableOpacity>
        </View>

        {/* 2. NEXT APPOINTMENT CARD (Nhắc lịch) */}
        {nextAppointment ? (
          <Surface style={styles.appointmentCard} elevation={2}>
            <View style={styles.aptHeader}>
              <Text variant="titleMedium" style={{ color: theme.colors.primary, fontWeight: 'bold' }}>
                Lịch khám sắp tới
              </Text>
              <MaterialCommunityIcons name="bell-ring" size={20} color="#FF9800" />
            </View>

            <View style={styles.aptBody}>
              <View style={styles.aptDate}>
                <Text variant="headlineMedium" style={{ fontWeight: 'bold', color: '#333' }}>
                  {dayjs(nextAppointment.start_time).format('DD')}
                </Text>
                <Text variant="bodyMedium">
                  Thg {dayjs(nextAppointment.start_time).format('MM')}
                </Text>
              </View>
              <View style={styles.aptInfo}>
                <Text variant="titleMedium" numberOfLines={1} style={{ fontWeight: 'bold' }}>
                  {nextAppointment.doctor?.user?.full_name || 'Bác sĩ'}
                </Text>
                <Text variant="bodyMedium" style={{ color: '#666' }}>
                  {dayjs(nextAppointment.start_time).format('HH:mm')} - {nextAppointment.room?.name}
                </Text>
                <Text variant="bodySmall" style={{ color: '#2196F3', marginTop: 4 }}>
                  {nextAppointment.appointment_type}
                </Text>
              </View>
            </View>
          </Surface>
        ) : (
          // Nếu không có lịch thì hiện Banner
          <View style={styles.bannerContainer}>
            <Text style={{ color: '#fff', textAlign: 'center', opacity: 0.9 }}>
              Chúc bạn một ngày tốt lành!
            </Text>
          </View>
        )}
      </View>

      {/* 3. MENU GRID (Chức năng chính) */}
      <View style={styles.sectionContainer}>
        <Text variant="titleMedium" style={styles.sectionTitle}>Dịch vụ của bạn</Text>
        <View style={styles.gridContainer}>
          {features.map((item) => (
            <TouchableOpacity
              key={item.id}
              style={styles.gridItem}
              onPress={() => navigation.navigate(item.screen)}
            >
              <View style={[styles.iconContainer, { backgroundColor: item.color + '20' }]}>
                <MaterialCommunityIcons name={item.icon as any} size={32} color={item.color} />
              </View>
              <Text style={styles.gridLabel}>{item.title}</Text>
            </TouchableOpacity>
          ))}
        </View>
      </View>

      {/* 4. PROMOTION / NEWS (Tin tức) */}
      <View style={styles.sectionContainer}>
        <Text variant="titleMedium" style={styles.sectionTitle}>Tin tức y tế</Text>
        <ScrollView horizontal showsHorizontalScrollIndicator={false} style={{ marginTop: 8 }}>
          {[1, 2, 3].map((i) => (
            <Card key={i} style={styles.newsCard}>
              <Card.Cover source={{ uri: 'https://picsum.photos/300/150' }} style={{ height: 120 }} />
              <Card.Content style={{ padding: 8 }}>
                <Text variant="titleSmall" numberOfLines={2} style={{ fontWeight: 'bold' }}>
                  5 cách phòng tránh bệnh cúm mùa hiệu quả
                </Text>
              </Card.Content>
            </Card>
          ))}
        </ScrollView>
      </View>

      <View style={{ height: 20 }} />
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f8f9fa',
  },
  headerContainer: {
    padding: 20,
    paddingTop: 50,
    borderBottomLeftRadius: 30,
    borderBottomRightRadius: 30,
    marginBottom: 20,
  },
  headerContent: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 20,
  },
  // Style cho Card nhắc lịch
  appointmentCard: {
    backgroundColor: '#fff',
    borderRadius: 16,
    padding: 16,
    // Hiệu ứng đổ bóng
    marginTop: 10,
    marginBottom: -40, // Đẩy card lồi xuống dưới
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.1,
    shadowRadius: 8,
    elevation: 5,
  },
  bannerContainer: {
    paddingBottom: 20,
    marginBottom: 0,
  },
  aptHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 12,
  },
  aptBody: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  aptDate: {
    backgroundColor: '#f0f7ff',
    borderRadius: 12,
    padding: 12,
    alignItems: 'center',
    marginRight: 16,
    minWidth: 70,
  },
  aptInfo: {
    flex: 1,
  },
  // Style cho Grid Menu
  sectionContainer: {
    marginTop: 30, // Chừa chỗ cho card lồi xuống
    paddingHorizontal: 20,
    marginBottom: 10,
  },
  sectionTitle: {
    fontWeight: 'bold',
    marginBottom: 16,
    color: '#333',
  },
  gridContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
  },
  gridItem: {
    width: (width - 60) / 2, // Chia đôi màn hình trừ padding
    backgroundColor: '#fff',
    borderRadius: 16,
    padding: 16,
    alignItems: 'center',
    marginBottom: 16,
    // Shadow nhẹ
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.05,
    shadowRadius: 4,
    elevation: 2,
  },
  iconContainer: {
    width: 56,
    height: 56,
    borderRadius: 28,
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom: 12,
  },
  gridLabel: {
    fontWeight: '600',
    color: '#333',
  },
  // News
  newsCard: {
    width: 200,
    marginRight: 16,
    backgroundColor: '#fff',
  },
});
